import {createStore} from 'redux';
import {reducer} from './reducer';
import storage from 'redux-persist/lib/storage';
import {persistReducer} from 'redux-persist';
import {comibeReducer} from "@reduxjs/toolkit";

const persistConfig = {
    key: "root",
    version: 1,
    storage
}
const reducerPersist = comibeReducer({
    reducer: reducer
})
const persistedReducer = persistReducer(persistConfig, reducerPersist); 

export const store = createStore(persistReducer);
